mapper.select:

select 

from press_release_access_history
where ID = #{id,jdbcType=INTEGER}
  and PRESS_RELEASE_ID = #{pressReleaseId,jdbcType=INTEGER}

mapper.delete:

delete from press_release_access_history
where ID = #{id,jdbcType=INTEGER}
  and PRESS_RELEASE_ID = #{pressReleaseId,jdbcType=INTEGER}
